import React, { useEffect, useState } from "react";
import { getPatientProfile, updatePatientProfile } from "../services/api";
import DashPatient from "../components/DashPatient";
import ProfileEditForm from "../components/ProfileEditForm";
import "../styles/DashPatient.css";

const PatientProfilePage = () => {
  const patientId = 1; // Example patient ID
  const [patient, setPatient] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const fetchPatient = async () => {
      try {
        const data = await getPatientProfile(patientId);
        setPatient(data);
      } catch (error) {
        console.error("Error fetching patient data:", error);
      }
    };

    fetchPatient();
  }, [patientId]);

  const handleEdit = () => setIsEditing(true);

  const handleSave = async (updatedData) => {
    try {
      const updatedPatient = await updatePatientProfile(patientId, updatedData);
      setPatient(updatedPatient);
      setIsEditing(false);
    } catch (error) {
      console.error("Error saving patient data:", error);
    }
  };

  const handleCancel = () => setIsEditing(false);

  if (!patient) return <div>Loading...</div>;

  return (
    <div className="patient-profile-page">
      {isEditing ? (
        <ProfileEditForm
          patient={patient}
          onSave={handleSave}
          onCancel={handleCancel}
        />
      ) : (
        <DashPatient patient={patient} onEdit={handleEdit} />
      )}
    </div>
  );
};

export default PatientProfilePage;
