import React, { useState } from "react";
import "../styles/DoctorCard.css";

const DoctorCard = ({ doctor }) => {
  const [currentDayIndex, setCurrentDayIndex] = useState(0); // Track the selected day
  const [visibleSlots, setVisibleSlots] = useState(3); // Number of initially visible slots

  const days = [
    { date: "2024-12-15", label: "dimanche" },
    { date: "2024-12-16", label: "lundi" },
    { date: "2024-12-17", label: "mardi" },
  ];

  const handleNextDay = () => {
    if (currentDayIndex < days.length - 1) {
      setCurrentDayIndex(currentDayIndex + 1);
      setVisibleSlots(3); // Reset visible slots for the new day
    }
  };

  const handlePreviousDay = () => {
    if (currentDayIndex > 0) {
      setCurrentDayIndex(currentDayIndex - 1);
      setVisibleSlots(3); // Reset visible slots for the new day
    }
  };

  const handleShowMoreSlots = () => {
    setVisibleSlots(visibleSlots + 3); // Increase the number of visible slots
  };

  return (
    <div className="doctor-card">
      {/* Left Section: Doctor Info */}
      <div className="doctor-profile">
        <img
          src="https://via.placeholder.com/100" // Placeholder for the doctor's image
          alt="Doctor"
          className="doctor-image"
        />
        <div className="doctor-info">
          <h3>{doctor.name}</h3>
          <p>{doctor.specialty}</p>
          <p>{doctor.city}</p>
          <button className="view-profile">Voir profil</button>
        </div>
      </div>

      {/* Right Section: Availability */}
      <div className="availability">
        <div className="day-navigation">
          <button onClick={handlePreviousDay} disabled={currentDayIndex === 0}>
            &lt;
          </button>
          <div className="day-info">
            <h4>{days[currentDayIndex].label}</h4>
            <p>{days[currentDayIndex].date}</p>
          </div>
          <button
            onClick={handleNextDay}
            disabled={currentDayIndex === days.length - 1}
          >
            &gt;
          </button>
        </div>

        <div className="slots">
          {doctor.availability
            .slice(0, visibleSlots)
            .map((slot) => (
              <button key={slot.id} className="slot">
                {slot.time}
              </button>
            ))}
          {visibleSlots < doctor.availability.length && (
            <button onClick={handleShowMoreSlots} className="more-slots">
              plus...
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default DoctorCard;
