import React, { useState } from "react";
import "../styles/DoctorCard.css";

const DoctorCard = ({ doctor }) => {
  const [currentDayIndex, setCurrentDayIndex] = useState(0); // Track the first day in the visible range
  const [visibleSlots, setVisibleSlots] = useState(3); // Number of initially visible slots

  // Generate an array of days starting from today
  const days = Array.from({ length: 30 }, (_, index) => {
    const date = new Date();
    date.setDate(date.getDate() + index);
    return {
      date: date.toISOString().split("T")[0], // Format: YYYY-MM-DD
      label: date.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "short" }), // Example: lundi 16 déc.
    };
  });

  // Get the current set of three days to display
  const visibleDays = days.slice(currentDayIndex, currentDayIndex + 3);

  const handleNextDays = () => {
    if (currentDayIndex + 3 < days.length) {
      setCurrentDayIndex(currentDayIndex + 3);
      setVisibleSlots(3); // Reset visible slots for the new range
    }
  };

  const handlePreviousDays = () => {
    if (currentDayIndex - 3 >= 0) {
      setCurrentDayIndex(currentDayIndex - 3);
      setVisibleSlots(3); // Reset visible slots for the previous range
    }
  };

  const handleShowMoreSlots = () => {
    setVisibleSlots(visibleSlots + 3); // Show 3 more slots
  };

  return (
    <div className="doctor-card">
      {/* Left Section: Doctor Info */}
      <div className="doctor-profile">
        <img
          src="https://via.placeholder.com/100" // Replace with dynamic doctor image
          alt="Doctor"
          className="doctor-image"
        />
        <div className="doctor-info">
          <h3>{doctor.name}</h3>
          <p>{doctor.specialty}</p>
          <p>{doctor.city}</p>
          <button className="view-profile">Voir profil</button>
        </div>
      </div>

      {/* Right Section: Availability */}
      <div className="availability">
        <div className="day-navigation">
          <button onClick={handlePreviousDays} disabled={currentDayIndex === 0}>
            &lt;
          </button>
          {visibleDays.map((day) => (
            <div className="day-info" key={day.date}>
              <h4>{day.label}</h4>
            </div>
          ))}
          <button
            onClick={handleNextDays}
            disabled={currentDayIndex + 3 >= days.length}
          >
            &gt;
          </button>
        </div>

        <div className="slots">
          {visibleDays.map((day) => (
            <div className="day-slots" key={day.date}>
              <h4>{day.label}</h4>
              {doctor.availability.slice(0, visibleSlots).map((slot) => (
                <button key={`${day.date}-${slot.id}`} className="slot">
                  {slot.time}
                </button>
              ))}
              {visibleSlots < doctor.availability.length && (
                <button onClick={handleShowMoreSlots} className="more-slots">
                  plus...
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DoctorCard;
