import React from 'react';
import { Link } from 'react-router-dom'; // For navigation to the "Book Appointment" page
import { FaSignOutAlt } from 'react-icons/fa'; // Import the logout icon from react-icons
import '../styles/PatientSidebar.css'; // Import CSS for the sidebar

const PatientSidebar = ({ setActiveSection }) => {
    const handleLogout = () => {
        // Logic for logging out the user (e.g., clearing tokens, session, etc.)
        console.log("Logged out!");
    };

    return (
        <div className="sidebar-container">
            {/* Main Sidebar Menu */}
            <ul>
                <li onClick={() => setActiveSection('patientInfo')} className="sidebar-item">Patient Info</li>
                <li onClick={() => setActiveSection('medicalRecords')} className="sidebar-item">Medical Records</li>
                <li onClick={() => setActiveSection('appointments')} className="sidebar-item">Appointments</li>
            </ul>

            {/* Separator */}
            <div className="sidebar-separator"></div>

            {/* Footer with Book Appointment and Logout */}
            <div className="sidebar-footer">
                <Link to="/book-appointment">
                    <button className="sidebar-btn-book">
                        <i className="fa fa-calendar-plus"></i> Book Appointment
                    </button>
                </Link>

                {/* Logout as a red link */}
                <Link to="#" onClick={handleLogout} className="sidebar-logout-link">
                    <FaSignOutAlt /> Logout
                </Link>
            </div>
        </div>
    );
};

export default PatientSidebar;
