import React, { useState } from 'react';

const PatientInfo = ({ patient, onUpdatePatientInfo }) => {
    const [editedPatient, setEditedPatient] = useState({ ...patient });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedPatient((prev) => ({ ...prev, [name]: value }));
    };

    const handleSave = () => {
        if (onUpdatePatientInfo) {
            onUpdatePatientInfo(editedPatient); // Call the function passed from parent
        } else {
            console.error('onUpdatePatientInfo function is not defined');
        }
    };

    const handleCancel = () => {
        setEditedPatient({ ...patient });
    };

    return (
        <div className="patient-info">
            <h2>Patient Information</h2>
            <form>
                <div>
                    <label>First Name</label>
                    <input
                        type="text"
                        name="firstName"
                        value={editedPatient.firstName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Last Name</label>
                    <input
                        type="text"
                        name="lastName"
                        value={editedPatient.lastName}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Email</label>
                    <input
                        type="email"
                        name="email"
                        value={editedPatient.email}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Phone</label>
                    <input
                        type="text"
                        name="phone"
                        value={editedPatient.phone}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Address</label>
                    <input
                        type="text"
                        name="address"
                        value={editedPatient.address}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>City</label>
                    <input
                        type="text"
                        name="city"
                        value={editedPatient.city}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Country</label>
                    <input
                        type="text"
                        name="country"
                        value={editedPatient.country}
                        onChange={handleChange}
                    />
                </div>
                <button type="button" onClick={handleSave}>
                    Save
                </button>
                <button type="button" onClick={handleCancel}>
                    Cancel
                </button>
            </form>
        </div>
    );
};

export default PatientInfo;
