import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PatientInfo from '../components/PatientInfo';
import MedicalRecords from '../components/MedicalRecords';
import Appointments from '../components/Appointments';
import PatientSidebar from '../components/PatientSidebar';

const PatientDashboard = () => {
    const [patient, setPatient] = useState(null);
    const [medicalRecords, setMedicalRecords] = useState([]);
    const [appointments, setAppointments] = useState([]);
    const [activeSection, setActiveSection] = useState('patientInfo'); // Default to Patient Info
    const [loading, setLoading] = useState(true); // Loading state

    useEffect(() => {
        const fetchPatientInfo = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/patients/1'); // replace with actual patient ID
                setPatient(response.data);
            } catch (error) {
                console.error('Error fetching patient data:', error);
            }
        };

        const fetchMedicalRecords = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/medical-records/patient/1');
                setMedicalRecords(response.data);
            } catch (error) {
                console.error('Error fetching medical records:', error);
            }
        };

        const fetchAppointments = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/appointments/patient/1');
                setAppointments(response.data);
            } catch (error) {
                console.error('Error fetching appointments:', error);
            }
        };

        fetchPatientInfo();
        fetchMedicalRecords();
        fetchAppointments();
        setLoading(false); // After fetching the data, set loading to false
    }, []);

    if (loading) {
        return <div>Loading...</div>; // Show loading state
    }

    const renderSection = () => {
        switch (activeSection) {
            case 'patientInfo':
                return <PatientInfo patient={patient} />;
            case 'medicalRecords':
                return <MedicalRecords records={medicalRecords} />;
            case 'appointments':
                return <Appointments appointments={appointments} />;
            default:
                return <PatientInfo patient={patient} />;
        }
    };

    return (
        <div className="patient-dashboard">
            <div className="sidebar-container">
                <PatientSidebar setActiveSection={setActiveSection} />
            </div>
            <div className="main-content">
                {renderSection()}
            </div>
        </div>
    );
};

export default PatientDashboard;
