import React from 'react';
import '../styles/PatientInfoTab.css';  // Importing the CSS for PatientInfoTab

function PatientInfoTab({ profile, handleProfileUpdate }) {
    const handleChange = (e) => {
        const { name, value } = e.target;
        handleProfileUpdate({ [name]: value });
    };

    return (
        <div>
            <h2>Informations du patient</h2>
            <form>
                <label>Name</label>
                <input
                    type="text"
                    name="name"
                    value={profile.name}
                    onChange={handleChange}
                />
                <label>Email</label>
                <input
                    type="email"
                    name="email"
                    value={profile.email}
                    onChange={handleChange}
                />
                <label>Age</label>
                <input
                    type="number"
                    name="age"
                    value={profile.age}
                    onChange={handleChange}
                />
                <label>Gender</label>
                <select
                    name="gender"
                    value={profile.gender}
                    onChange={handleChange}
                >
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                </select>
                <label>Height</label>
                <input
                    type="number"
                    name="height"
                    value={profile.height}
                    onChange={handleChange}
                />
                <label>Weight</label>
                <input
                    type="number"
                    name="weight"
                    value={profile.weight}
                    onChange={handleChange}
                />
                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
}

export default PatientInfoTab;
